function showReferenceMenu(elem, data){


	document.querySelectorAll(elem + ' .pure-tree.main-tree').forEach(e => e.remove());

	var content = '<ul class="pure-tree main-tree">';

	data.forEach(function (item, i) {
		var type = (item.text ? 'text' : (item.video ? 'video' : (item.audio ? 'audio' : (item.pdf ? 'pdf' : (item.img ? 'img' : '')))));

		if(item.href){
			content += '<li class="pure-tree_link"><a href="' + item.href + '" target="_blank">' + item.name + '</a>';
		}else if(item.video || item.audio || item.text || item.pdf || item.img){
			content += '<li><input type="checkbox" id="trigger-' + i + '"><label for="trigger-' + i + '" class="top-tree ' + type +'" data-type="' + type + '" data-id="' + i + '">' + item.name + '</label>';
		}else{
			if(item.data){
				content += '<li><input type="checkbox" id="trigger-' + i + '"><label for="trigger-' + i + '" class="top-tree">' + item.name + '</label>';
				content += '<ul class="pure-tree">';
				item.data.forEach(function (item_j, j) {
					var type_j = (item_j.text ? 'text' : (item_j.video ? 'video' : (item_j.audio ? 'audio' : (item_j.pdf ? 'pdf' : (item_j.img ? 'img' : '')))));


					if(item_j.href){
						content += '<li class="pure-tree_link"><a href="' + item_j.href + '" target="_blank">' + item_j.name + '</a>';
					}else if(item_j.video || item_j.audio || item_j.text || item_j.pdf || item_j.img){
						content += '<li id="asd"><input type="checkbox" id="trigger-' + i + '_' + j + '"><label for="trigger-' + i + '_' + j + '" class="' + type_j +'" data-type="' + type_j + '" data-id="' + i + '_' + j + '">' + item_j.name + '</label>';
					}

				});
				content += '</ul>';
			}
		}

		content += '</li>';
	});

	content += '</ul>';

	$(elem).append(content);

	$(elem).on('click', '[data-type]', function (e) {
		var type = $(this).attr('data-type');
		var id = $(this).attr('data-id');
		hidenReferenceContent(elem + '_content');
		showReferenceContent(elem + '_content', type, id, data);
	});
}

function showReferenceContent(elem, type, id, dataM){
	$('.overlay--galary').css({
		'transform' : 'none'
	});

	var content = '';
	var IDs = id.split('_');
	var data = [];

	if(IDs.length > 1){
		data = dataM[IDs[0]].data[IDs[1]];
	}else{
		data = dataM[IDs[0]];
	}

	switch(type){
        case 'img':
            content += data.title ? '<h2>' + data.title + '</h2>' : '';
			content += '<img class="img_ref" src="media/image/' + data.img + '"/>';
            content += data.desc ? '<div class="video_desc">' + data.desc + '</div>' : '';
            break;
		case 'text':
			content += data.text;
		    break;
		case 'pdf':
			content += '<iframe class="PDFReader" src="PDFReader/web/viewer.html?file=../files/' + data.pdf + '"></iframe>';
		    break;
		case 'video':
			if(data.video){
				var videoID = data.video.split(' ');
				videoID.forEach(function (item, i) {
					content += getMedia('video', item);
				});
			}

		    break;
		case 'audio':
			if(data.audio){
				var videoID = data.audio.split(' ');
				videoID.forEach(function (item, i) {
					content += getMedia('audio', item);
				});
			}
		    break;
	}


	$(elem).append(content);


}

function hidenReferenceContent(elem){
	$(elem).empty();
}
